import React from 'react';
import {Text} from 'react-native';

const Mensagem = (props) => {
    if (props.IMC<17) return <Text style={{fontSize: 18, fontWeight:'bold', color:'red'}}>Muito abaixo do peso</Text>
    if (props.IMC<18.49) return <Text style={{fontSize: 18, fontWeight:'bold', color:'blue'}}>Abaixo do peso</Text>
    if (props.IMC<24.99) return <Text style={{fontSize: 18, fontWeight:'bold', color:'red'}}>Peso normal</Text>
    if (props.IMC<29.99) return <Text style={{fontSize: 18, fontWeight:'bold', color:'red'}}>Acima do peso</Text>
    if (props.IMC<34.99) return <Text style={{fontSize: 18, fontWeight:'bold', color:'red'}}>Obesidade I</Text>
    if (props.IMC<39.99) return <Text style={{fontSize: 18, fontWeight:'bold', color:'red'}}>Obesidade I severa</Text>
    if (props.IMC>40) return <Text style={{fontSize: 18, fontWeight:'bold', color:'red'}}>Obesidade II m√≥rbida</Text>
}
