import React, {Component} from "react";
import { Alert, StyleSheet, View, Text, TouchableHighlight, TouchableOpacity, TouchableNativeFeedback } from "react-native";

export default class Tocavel extends Component {
        acaoBotao = ()=>{
            alert('Apertei!')
        }

    render(){
        return(
            <View style={estilos.container}>
                <TouchableHighlight onPress={this.acaoBotao} underlayColor='#000'>
                    <View style={estilos.botao}>
                        <Text style={estilos.textoBotao}>
                            TouchableHighlight
                        </Text>
                    </View>
                </TouchableHighlight>

                <TouchableOpacity onPress={this.acaoBotao}>
                    <View style={estilos.botao}>
                        <Text style={estilos.textoBotao}>
                            TouchableHighlight
                        </Text>
                    </View>
                </TouchableOpacity>
            </View>
        )
    }
}    

const estilos = StyleSheet.create(
    {
        container: {
            paddingTop:0,
            flex: 1,
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center'
        },
        botao:{
            marginBottom: 30,
            width: 200,
            alignItems: 'center',
            backgroundColor: '#2196F3'
        },
        textoBotao:{
            padding: 20,
            color: '#FFF'
        }
    }
)
